mapper.select:
select count(*) from users where
${param1} = #{param2}
${param1} like '%' || #{param2} || '%'
and user_separator = #{param3}

select * from (select rownum as rn, a.* from (select * from users where
${param3} = #{param4}
${param3} like '%' || #{param4} || '%'
and user_separator = #{param5}
order by user_no desc) a) b where b.rn between #{param1} and #{param2}

mapper.update:
update
	users
set
	email = #{email}
where
	user_no = #{user_no}

update
	users
set
	password = #{password}
where
	user_no = #{user_no}

mapper.delete:
delete from
	scrap
where
	scrap_separator = #{param1}
	and scrap_referer_no = #{param2}

delete from
	comments
where
	comment_referer_sep = #{param1}
	and comment_referer_no = #{param2}

delete from
	photo
where
	photo_referer_sep = #{param1}
	and photo_referer_no = #{param2}

